package com.example.testlongtextsincompose

import android.os.Bundle
import androidx.activity.ComponentActivity
import androidx.activity.compose.setContent
import androidx.compose.foundation.isSystemInDarkTheme
import androidx.compose.foundation.layout.Arrangement
import androidx.compose.foundation.layout.Column
import androidx.compose.foundation.layout.IntrinsicSize
import androidx.compose.foundation.layout.Row
import androidx.compose.foundation.layout.Spacer
import androidx.compose.foundation.layout.fillMaxSize
import androidx.compose.foundation.layout.fillMaxWidth
import androidx.compose.foundation.layout.height
import androidx.compose.foundation.layout.size
import androidx.compose.foundation.layout.width
import androidx.compose.material.Button
import androidx.compose.material.MaterialTheme
import androidx.compose.material.Text
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.platform.LocalConfiguration
import androidx.compose.ui.text.TextStyle
import androidx.compose.ui.text.font.FontFamily
import androidx.compose.ui.text.font.FontStyle
import androidx.compose.ui.unit.dp
import androidx.navigation.NavController
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import androidx.navigation.compose.rememberNavController

private const val LOOONG_STRING = "data:image/png;base64,"

class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContent {
            MaterialTheme {
                AppScreens()
            }
        }
    }
}

@Composable
private fun AppScreens() {
    val navController = rememberNavController()
    NavHost(navController = navController, startDestination = Screen.MainScreen.route) {
        composable(route = Screen.MainScreen.route) {
            MainScreen(navController)
        }

        composable(route = Screen.InfiniteBoundsText.route) {
            InfiniteBoundsTextScreen()
        }

        composable(route = Screen.InfiniteBoundsRowButSmallerText.route) {
            InfiniteBoundsRowButSmallerTextScreen()
        }

        composable(route = Screen.ScreenWidthText.route) {
            ScreenWidthTextScreen()
        }
    }
}

@Composable
private fun InfiniteBoundsTextScreen() {
    // Nothing fancy. I expect this to crash the app.
    Row(Modifier.width(IntrinsicSize.Max)) {
        ReallyLongText()
    }
}

@Composable
private fun InfiniteBoundsRowButSmallerTextScreen() {
    // Nothing fancy. I expect this to bring the app to a halt.
    Row(Modifier.width(IntrinsicSize.Max)) {
        ReallyLongText(Modifier.size(40.dp))
    }
}

@Composable
private fun ScreenWidthTextScreen() {
    // Nothing fancy. I expect this to crash the app or bring it to a halt.
    Row(Modifier.width(LocalConfiguration.current.screenWidthDp.dp)) {
        ReallyLongText()
    }
}

@Composable
private fun MainScreen(navController: NavController) {
    Column(
        Modifier.fillMaxSize(),
        verticalArrangement = Arrangement.Center
    ) {
        Button(onClick = {
            navController.navigate(Screen.InfiniteBoundsText.route)
        }) {
            Column {
                Text("Try a looong text with infinite width")

                CodeStyledText(
                    """
Row(Modifier.width(IntrinsicSize.Max)) {
    Text(text = looongString...)
})
"""
                )

            }
        }

        Spacer(Modifier.height(20.dp))

        Button(onClick = {
            navController.navigate(Screen.InfiniteBoundsRowButSmallerText.route)
        }) {
            Column {
                Text("Try a looong text with infinite parent bounds but small text size")

                CodeStyledText(
                    """
Row(Modifier.width(IntrinsicSize.Max)) {
    Text(
        modifier = Modifier.size(40.dp),
        text = looongString...
    )
})
"""
                )

            }
        }

        Spacer(Modifier.height(16.dp))

        Button(onClick = {
            navController.navigate(Screen.ScreenWidthText.route)
        }) {
            Column {
                Text("Try a looong text expecting to only occupy screen width")

                CodeStyledText(
                    """
Row(LocalConfiguration
    .current
    .screenWidthDp.dp)) {
    Text(text = looongString...)
})
"""
                )
            }
        }
    }
}

@Composable
private fun CodeStyledText(text: String) {
    Text(
        modifier = Modifier.fillMaxWidth(),
        text = text,
        style = TextStyle(
            color = Color(0xFF5C4033),
            background = Color.LightGray
        ),
        fontStyle = FontStyle.Italic,
        fontFamily = FontFamily.Monospace,
    )
}

@Composable
private fun ReallyLongText(modifier: Modifier = Modifier) {
    Text(
        modifier = modifier,
        text = LOOONG_STRING,
        color = if (isSystemInDarkTheme()) Color.White else Color.Black
    )
}
